$('#u-error').hide();
$('#p-error').hide();

$('#sub').click(function(e){
	e.preventDefault();
		
	$('#u-error').hide();
   $('#p-error').hide();

	let u = $('#USER').val(), p = $('#PASSWORD').val();	
	console.log(`u: ${u}, p: ${p}`);
	
	if(u == "" || p == ""){
		if(u == "") $('#u-error').fadeIn();
		if(p == "") $('#p-error').fadeIn();
	}
	else{
		$('#sform').submit();
	}
});

